package loopingstatements;

public class Breakstatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=10; i++)
		{
			if(i==6)
			{
				break;  
	// once i value becomes 5 if condition is satisfied then break command will execute.
	//The loop will break and for loop will be exited.
			}
			System.out.println(i);
		}

	}

}
